mapper.select:
SELECT o.order_no, o.order_type, o.date, g.g_code, g.g_name, g.specification, g.py_code, u.gu_name AS unitName, og.price, og.good_num AS count, og.total_price, o.wh_name, o.source_name FROM tb_orders o LEFT JOIN tb_order_goods og ON og.order_id = o.id LEFT JOIN tb_goods_info g ON g.id = og.good_id LEFT JOIN tb_goods_unit u ON u.id = g.unit WHERE o.order_type IN (1, 4) AND o.status = 2
AND g.id = #{goodId}
ORDER BY o.order_date DESC
